import React, { useState, useEffect } from 'react'
import { Link, useParams } from 'react-router-dom';
import sheetsService from '../services/gsheets-service'

const SearchScreen = () => {
  // const { className } = useParams();
  const [advisory, setAdvisory] = useState({});
  // const [searchResults, setSearchResults] = useState({});

  const loadSheet = async () => {
    const doc = await sheetsService.getSpreadSheet();
    setAdvisory(doc);
  }

  useEffect(() => {
    loadSheet();
  });

  return (
    <div>
      <h2>Student Search</h2>
      <input className="form-control" type="text" placeholder="Student name"/>
      <button className="btn btn-primary"
              onClick={() => loadSheet()}>
        Search
      </button>

      <div>
        <ul className="list-group">
          {
            advisory.map(row =>
              <li className="list-group-item">
                <Link to={`/details/{someID}`}>
                  {`${row.name} ${row.Math} ${row.English} ${row.Spanish} `}
                </Link>
              </li>
            )
          }
          <li className="list-group-item">
            <Link to={`/details/{someID}`}>
              Kim
            </Link>
          </li>
          <li className="list-group-item">
            James
          </li>
          <li className="list-group-item">
            Carlos
          </li>
          <li className="list-group-item">
            Louis
          </li>
        </ul>
      </div>
    </div>
  )
}

export default SearchScreen;
